import 'mapbox-gl/dist/mapbox-gl.css';

function App() {
    const geolocateControlStyle = {
        right: 10,
        top: 10
      };
    const [viewport, setViewport] = useState({
        latitude: -1.264417,
        longitude: -78.608275,
        zoom: 10,
        width: "100vw",
        height: "100vh"
    });
    return (

        <div>
            <ReactMapGL {...viewport}
                mapboxApiAccessToken={
                    process.env.REACT_APP_MAPBOX_TOKEN
                }
                onViewportChange={
                    viewport => {
                        setViewport(viewport);
                    }
            }>
